user_service.go代码如下：
package main

import (
    "fmt"
    v1 "proto/api/v1"

    "github.com/gogo/protobuf/proto"
    "io/ioutil"
    "net/http"
)

func main() {
    //单独写回调函数
    http.HandleFunc("/post", getHandler)
    // addr：监听的地址
    // handler：回调函数
    _ = http.ListenAndServe("127.0.0.1:9090", nil)
}

func getHandler(w http.ResponseWriter, r *http.Request) {
    b, err := ioutil.ReadAll(r.Body)
    fmt.Println(r.PostForm.Get("name"))

    if err != nil {
        fmt.Println("读取内容错误:", err.Error())
    }
    req := &v1.UserListRequest{}
    err = proto.Unmarshal(b, req)

    if err != nil {
        fmt.Println("内容解析错误:", err.Error())
    }
    fmt.Println(req)

    resp := &v1.UserListResponse{
        List: []*v1.User{
            {
                Name: "张三",
                Age:  18,
                Sex:  1,
                City: "北京",
            },
            {
                Name: "李四",
                Age:  20,
                Sex:  0,
                City: "西安",
            },
            {
                Name: "王五",
                Age:  28,
                Sex:  1,
                City: "河南",
            },
        },
    }

    protoResp, _ := proto.Marshal(resp)
    w.Write(protoResp)
}

